import React, { useEffect, useState } from "react";
import axios from "axios";

function NewsList() {
  const [articles, setArticles] = useState([]);

  useEffect(() => {
    const options = {
      method: "GET",
      url: "https://cybersecurity-news.p.rapidapi.com/news",
      headers: {
        "x-rapidapi-key": "bcbd5c9dfcmsh54b7daa02a69f1bp16b4a1jsn78732abf5a63", // safer via env variable
        "x-rapidapi-host": "cybersecurity-news.p.rapidapi.com",
      },
    };

    async function fetchData() {
      try {
        const response = await axios.request(options);
        console.log(response.data);
        // Adjust below based on actual response structure
        setArticles(response.data.articles || response.data);
      } catch (error) {
        console.error(error);
      }
    }

    fetchData();
  }, []);

  return (
    <div>
      <h2>Cybersecurity News</h2>
      <ul>
        {articles.length > 0 ? (
          articles.map((article, idx) => (
            <li key={idx}>
              <a href={article.url || article.link} target="_blank" rel="noreferrer">
                {article.title}
              </a>
            </li>
          ))
        ) : (
          <p>No articles found.</p>
        )}
      </ul>
    </div>
  );
}

export default NewsList;
