import React from "react";
import { BrowserRouter as Router, Routes, Route, Link, Navigate, useLocation } from "react-router-dom";

const NEWS_API_KEY = "9ec3cd1c0603415fa22623a3313343bf";
const NEWS_API_URL = `https://newsapi.org/v2/everything?q=cyber%20security&language=en&sortBy=publishedAt&apiKey=${NEWS_API_KEY}`;
const BACKEND_PROXY_URL = "http://localhost:4000/api/vulnerabilities"; // Your backend proxy for vulnerabilities

function extractCvssInfo(metrics) {
  if (!metrics) return {};
  const cvssV3 = metrics["cvssV3"];
  const cvssV2 = metrics["cvssV2"];
  if (cvssV3) {
    return {
      score: cvssV3.baseScore,
      severity: cvssV3.baseSeverity,
      attackVector: cvssV3.attackVector,
    };
  } else if (cvssV2) {
    return {
      score: cvssV2.baseScore,
      severity: cvssV2.severity,
      attackVector: cvssV2.accessVector,
    };
  }
  return {};
}

const placeholderNewsImage = "https://images.unsplash.com/photo-1519389950473-47ba0277781c?auto=format&fit=crop&w=150&q=80";

{newsList.map((news, i) => (
  <div
    key={i}
    style={{
      marginBottom: "20px",
      borderBottom: "1px solid #ddd",
      paddingBottom: "15px",
      display: "flex",
      gap: "15px",
    }}
  >
    <img
      src={news.urlToImage || placeholderNewsImage}
      alt="news"
      style={{ width: 150, height: 100, objectFit: "cover", borderRadius: "6px" }}
    />
    <div>
      <h3 style={{ color: "#007acc" }}>{news.title}</h3>
      <p style={{ fontSize: "14px", color: "#555" }}>
        {news.source?.name} | {new Date(news.publishedAt).toLocaleString()}
      </p>
      <p style={{ fontSize: "15px", color: "#333" }}>{news.description || news.content}</p>
      <a href={news.url} target="_blank" rel="noopener noreferrer" style={{ color: "#007acc" }}>
        Read full article
      </a>
    </div>
  </div>
))}


function News() {
  const [newsList, setNewsList] = React.useState([]);
  const [loading, setLoading] = React.useState(false);
  const [error, setError] = React.useState(null);

  const placeholderNewsImage = "https://via.placeholder.com/150x100?text=No+Image";

  React.useEffect(() => {
    async function fetchNews() {
      setLoading(true);
      setError(null);
      try {
        const response = await fetch(NEWS_API_URL);
        if (!response.ok) throw new Error("Failed to fetch news");
        const data = await response.json();
        setNewsList(data.articles || []);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }
    fetchNews();
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h2>Latest Cybersecurity News</h2>
      {loading && <p>Loading news...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}
      {!loading && newsList.length === 0 && <p>No news articles found.</p>}

      {newsList.map((news, i) => (
        <div
          key={i}
          style={{
            marginBottom: "20px",
            borderBottom: "1px solid #ddd",
            paddingBottom: "15px",
            display: "flex",
            gap: "15px",
          }}
        >
          <img
            src={news.urlToImage || placeholderNewsImage}
            alt="news"
            style={{ width: 150, height: 100, objectFit: "cover", borderRadius: "6px" }}
          />
          <div>
            <h3 style={{ color: "#007acc" }}>{news.title}</h3>
            <p style={{ fontSize: "14px", color: "#555" }}>
              {news.source?.name} | {new Date(news.publishedAt).toLocaleString()}
            </p>
            <p style={{ fontSize: "15px", color: "#333" }}>{news.description || news.content}</p>
            <a href={news.url} target="_blank" rel="noopener noreferrer" style={{ color: "#007acc" }}>
              Read full article
            </a>
          </div>
        </div>
      ))}
    </div>
  );
}

function Vulnerabilities() {
  const [vulnerabilities, setVulnerabilities] = React.useState([]);
  const [loading, setLoading] = React.useState(false);
  const [error, setError] = React.useState(null);

  // Placeholder image for vulnerabilities (replace if real images available)
  const placeholderVulnImage = "https://via.placeholder.com/300x150?text=No+Image";

  React.useEffect(() => {
    async function fetchVulnerabilities() {
      setLoading(true);
      setError(null);
      try {
        const response = await fetch(BACKEND_PROXY_URL);
        if (!response.ok) throw new Error("Failed to fetch vulnerabilities");
        const data = await response.json();
        setVulnerabilities(data.result?.CVE_Items || []);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }
    fetchVulnerabilities();
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h2>Recent Vulnerabilities</h2>
      {loading && <p>Loading vulnerabilities...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}
      {!loading && vulnerabilities.length === 0 && <p>No vulnerabilities found.</p>}

      <div style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
        {vulnerabilities.map((item) => {
          const cve = item.cve;
          const cveId = cve.CVE_data_meta.ID;
          let description = cve.description.description_data[0]?.value || "No description available";
          if (description.length > 150) {
            description = description.slice(0, 147) + "...";
          }
          const { score, severity, attackVector } = extractCvssInfo(item.impact?.baseMetricV3 || item.impact?.baseMetricV2);

          return (
            <div
              key={cveId}
              style={{
                border: "1px solid #eee",
                borderRadius: "8px",
                boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
                padding: "15px",
                width: "300px",
                backgroundColor: "#fafafa",
                display: "flex",
                flexDirection: "column",
                justifyContent: "space-between",
              }}
            >
              <img
                src={placeholderVulnImage}
                alt="vulnerability"
                style={{ width: "100%", height: 150, objectFit: "cover", borderRadius: "6px", marginBottom: "12px" }}
              />

              <h3 style={{ fontSize: "18px", marginBottom: "8px" }}>
                <a href={`https://nvd.nist.gov/vuln/detail/${cveId}`} target="_blank" rel="noopener noreferrer" style={{ textDecoration: "none", color: "#007acc" }}>
                  {cveId}
                </a>
              </h3>
              <p style={{ fontSize: "14px", color: "#333", flexGrow: 1 }}>{description}</p>

              <div style={{ marginTop: "12px", fontSize: "14px", color: "#555" }}>
                <p><strong>CVSS Score:</strong> {score ?? "N/A"}</p>
                <p><strong>Severity:</strong> {severity ?? "N/A"}</p>
                <p><strong>Attack Vector:</strong> {attackVector ?? "N/A"}</p>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

function Navigation() {
  const location = useLocation();
  const linkStyle = { marginRight: 20, textDecoration: "none", fontWeight: "bold" };
  const activeStyle = { color: "#007acc", borderBottom: "2px solid #007acc" };

  return (
    <nav style={{ padding: "15px 20px", borderBottom: "1px solid #ccc", marginBottom: "20px" }}>
      <Link to="/news" style={{ ...linkStyle, ...(location.pathname === "/news" ? activeStyle : {}) }}>
        News
      </Link>
      <Link to="/vulnerabilities" style={{ ...linkStyle, ...(location.pathname === "/vulnerabilities" ? activeStyle : {}) }}>
        Vulnerabilities
      </Link>
    </nav>
  );
}

export default function App() {
  return (
    <Router>
      <div style={{ maxWidth: 960, margin: "0 auto", fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif", color: "#222" }}>
        <h1 style={{ textAlign: "center", paddingTop: "20px", paddingBottom: "10px", color: "#007acc" }}>
          Cybersecurity Dashboard
        </h1>
        <Navigation />
        <Routes>
          <Route path="/" element={<Navigate replace to="/news" />} />
          <Route path="/news" element={<News />} />
          <Route path="/vulnerabilities" element={<Vulnerabilities />} />
        </Routes>
      </div>
    </Router>
  );
}
