// Components/NewsList.js
import React, { useEffect, useState } from "react";
import axios from "axios";

function NewsList({ apiUrl, itemType }) {
  const [articles, setArticles] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const options = {
          method: "GET",
          url: apiUrl || "https://cybersecurity-news.p.rapidapi.com/news/guardian", // ✅ use prop if passed
          params: { newspageId: "guardian" },
          headers: {
            "X-RapidAPI-Key": "bcbd5c9dfcmsh54b7daa02a69f1bp16b4a1jsn78732abf5a63",
            "X-RapidAPI-Host": "cybersecurity-news.p.rapidapi.com",
          },
        };

        const res = await axios.request(options);

        console.log("API Response:", res.data);

        // ✅ Check if API returns array directly or inside an object
        if (Array.isArray(res.data)) {
          setArticles(res.data);
        } else if (res.data.articles) {
          setArticles(res.data.articles);
        } else {
          console.warn("Unexpected API response format:", res.data);
        }
      } catch (error) {
        console.error("Error fetching news:", error.response ? error.response.data : error.message);
      }
    };

    fetchData();
  }, [apiUrl]);

  return (
    <div style={{ padding: "20px" }}>
      <h2>{itemType ? itemType : "Cybersecurity News"}</h2>
      <ul>
        {articles.length > 0 ? (
          articles.map((article, index) => (
            <li key={index}>
              <a href={article.link || article.url} target="_blank" rel="noreferrer">
                {article.title}
              </a>
            </li>
          ))
        ) : (
          <p>No articles found.</p>
        )}
      </ul>
    </div>
  );
}

export default NewsList;
