import React from "react";
import { BrowserRouter as Router, Routes, Route, Link, Navigate, useLocation } from "react-router-dom";

const NEWS_API_KEY = "9ec3cd1c0603415fa22623a3313343bf";
const NEWS_API_URL =
  `https://newsapi.org/v2/everything?` +
  `q=cybersecurity OR "cyber security" OR hacking OR "data breach" OR "information security"&` +
  `language=en&sortBy=publishedAt&apiKey=${NEWS_API_KEY}`;

const BACKEND_PROXY_URL = "http://localhost:4000/api/vulnerabilities"; // Change to deployed URL in production

const cardStyle = {
  borderRadius: "12px",
  boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
  backgroundColor: "#fff",
  transition: "transform 0.2s ease, box-shadow 0.2s ease",
  cursor: "pointer",
  padding: "20px",
  marginBottom: "30px",
  color: "#222",
  textDecoration: "none",
  boxSizing: "border-box",
};

const cardHoverStyle = {
  transform: "translateY(-5px)",
  boxShadow: "0 8px 20px rgba(0,0,0,0.15)",
};

const severityIcons = {
  CRITICAL: "🔥",
  HIGH: "⚠️",
  MEDIUM: "ℹ️",
  LOW: "✅",
};

function News() {
  const [newsList, setNewsList] = React.useState([]);
  const [loading, setLoading] = React.useState(false);
  const [error, setError] = React.useState(null);

  const getRandomCybersecurityImage = () => {
    return `https://source.unsplash.com/random/600x460/?cybersecurity,technology,computer,security,hacking`;
  };

  React.useEffect(() => {
    async function fetchNews() {
      setLoading(true);
      setError(null);
      try {
        const response = await fetch(NEWS_API_URL);
        if (!response.ok) throw new Error("Failed to fetch news");
        const data = await response.json();
        setNewsList(data.articles || []);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }
    fetchNews();
  }, []);

  return (
    <div style={{ padding: "20px", backgroundColor: "#f9f9f9" }}>
      <h2 style={{ fontSize: "32px", fontWeight: "700", marginBottom: "30px", color: "#0056b3" }}>
        Latest Cybersecurity News
      </h2>
      {loading && <p style={{ fontSize: "18px" }}>Loading news...</p>}
      {error && <p style={{ color: "red", fontSize: "18px" }}>{error}</p>}
      {!loading && newsList.length === 0 && <p style={{ fontSize: "18px" }}>No news articles found.</p>}

      {newsList.map((news, i) => (
        <div
          key={i}
          style={{
            marginBottom: "20px",
            borderBottom: "1px solid #ddd",
            paddingBottom: "15px",
            display: "flex",
            gap: "15px",
            flexWrap: "wrap",
          }}
        >
          <img
            src={news.urlToImage || getRandomCybersecurityImage()}
            alt="news"
            style={{ width: "100%", maxWidth: 300, height: 160, objectFit: "cover", borderRadius: "6px" }}
            loading="lazy"
          />
          <div style={{ flex: "1 1 300px" }}>
            <h3 style={{ fontSize: "24px", marginBottom: "12px", color: "#004080", lineHeight: 1.2 }}>
              {news.title}
            </h3>
            <p style={{ fontSize: "16px", color: "#222", marginBottom: "12px" }}>
              {news.description || news.content}
            </p>
            <p style={{ fontSize: "14px", color: "#666", fontStyle: "italic" }}>
              {news.source?.name} | {new Date(news.publishedAt).toLocaleString()}
            </p>
          </div>
        </div>
      ))}
    </div>
  );
}

function Vulnerabilities() {
  const [packageName, setPackageName] = React.useState("jinja2");
  const [version, setVersion] = React.useState("2.4.1");
  const [ecosystem, setEcosystem] = React.useState("PyPI");

  const [vulnerabilities, setVulnerabilities] = React.useState([]);
  const [loading, setLoading] = React.useState(false);
  const [error, setError] = React.useState(null);

  const placeholderVulnImage = "https://via.placeholder.com/400x200?text=No+Image";

  React.useEffect(() => {
    async function fetchVulnerabilities() {
      setLoading(true);
      setError(null);
      try {
        const response = await fetch(BACKEND_PROXY_URL, {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ packageName, version, ecosystem }),
        });
        if (!response.ok) throw new Error("Failed to fetch vulnerabilities");
        const data = await response.json();
        setVulnerabilities(data.vulns || []);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }
    fetchVulnerabilities();
  }, [packageName, version, ecosystem]);

  return (
    <div style={{ padding: "20px", backgroundColor: "#f9f9f9" }}>
      <h2 style={{ fontSize: "32px", fontWeight: "700", marginBottom: "30px", color: "#0056b3" }}>
        Vulnerabilities for {packageName} version {version} ({ecosystem})
      </h2>

      {/* Optional: Responsive input form */}
      <form
        onSubmit={(e) => {
          e.preventDefault();
          // fetchVulnerabilities will run due to dependency change
        }}
        style={{
          marginBottom: 30,
          display: "flex",
          flexWrap: "wrap",
          gap: 15,
          maxWidth: 600,
        }}
      >
        <input
          type="text"
          placeholder="Package Name"
          value={packageName}
          onChange={(e) => setPackageName(e.target.value)}
          style={{ flex: "1 1 150px", padding: 10, fontSize: 16, borderRadius: 6, border: "1px solid #ccc" }}
          required
        />
        <input
          type="text"
          placeholder="Version"
          value={version}
          onChange={(e) => setVersion(e.target.value)}
          style={{ flex: "1 1 100px", padding: 10, fontSize: 16, borderRadius: 6, border: "1px solid #ccc" }}
          required
        />
        <input
          type="text"
          placeholder="Ecosystem"
          value={ecosystem}
          onChange={(e) => setEcosystem(e.target.value)}
          style={{ flex: "1 1 120px", padding: 10, fontSize: 16, borderRadius: 6, border: "1px solid #ccc" }}
          required
        />
        <button
          type="submit"
          style={{
            padding: "10px 20px",
            fontSize: 16,
            fontWeight: "bold",
            borderRadius: 6,
            border: "none",
            backgroundColor: "#0056b3",
            color: "white",
            cursor: "pointer",
            flex: "1 1 100px",
            minWidth: 120,
          }}
        >
          Search
        </button>
      </form>

      {loading && <p style={{ fontSize: "18px" }}>Loading vulnerabilities...</p>}
      {error && <p style={{ color: "red", fontSize: "18px" }}>{error}</p>}
      {!loading && vulnerabilities.length === 0 && <p style={{ fontSize: "18px" }}>No vulnerabilities found.</p>}

      <div
        style={{
          display: "flex",
          flexWrap: "wrap",
          gap: "20px",
          justifyContent: "center",
        }}
      >
        {vulnerabilities.map((vuln) => {
  const severity = vuln.severity || "UNKNOWN";
  const severityKey = typeof severity === "string" ? severity.toUpperCase() : "UNKNOWN";
  const icon = severityIcons[severityKey] || "❓";

          return (
            <div
              key={vuln.id}
              style={{
                ...cardStyle,
                width: "100%",
                maxWidth: 400,
                display: "flex",
                flexDirection: "column",
                backgroundColor: "#fff",
                cursor: "pointer",
                boxSizing: "border-box",
              }}
            >
              <img
  src="https://source.unsplash.com/400x200/?cybersecurity,security,computer"
  alt="vulnerability"
  style={{
    width: "100%",
    height: 200,
    objectFit: "cover",
    borderRadius: "12px",
    marginBottom: "15px",
    boxShadow: "0 3px 12px rgba(0,0,0,0.15)",
  }}
  loading="lazy"
  draggable={false}
/>

              <h3 style={{ fontSize: "22px", marginBottom: "12px", color: "#004080", display: "flex", alignItems: "center", gap: '10px' }}>
                <span>{icon}</span>
                <a
                  href={`https://osv.dev/vulnerability/${vuln.id}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  style={{ textDecoration: "none", color: "#007acc" }}
                >
                  {vuln.id}
                </a>
              </h3>
              <p style={{ fontSize: "16px", color: "#333", marginBottom: "20px", lineHeight: "1.4" }}>
                {vuln.summary || "No description available"}
              </p>
            </div>
          );
        })}
      </div>
    </div>
  );
}

function Navigation() {
  const location = useLocation();
  const linkStyle = { marginRight: 30, textDecoration: "none", fontWeight: "700", fontSize: "18px" };
  const activeStyle = {
    color: "#007acc",
    borderBottom: "3px solid #007acc",
    paddingBottom: "4px",
    transition: "all 0.3s ease",
  };

  return (
    <nav
      style={{
        padding: "20px 40px",
        borderBottom: "2px solid #ccc",
        marginBottom: "40px",
        backgroundColor: "#f0f8ff",
        fontWeight: "600",
        fontSize: "20px",
      }}
    >
      <Link to="/news" style={{ ...linkStyle, ...(location.pathname === "/news" ? activeStyle : {}) }}>
        News
      </Link>
      <Link to="/vulnerabilities" style={{ ...linkStyle, ...(location.pathname === "/vulnerabilities" ? activeStyle : {}) }}>
        Vulnerabilities
      </Link>
    </nav>
  );
}

export default function App() {
  return (
    <Router>
      <div
        style={{
          maxWidth: 1024,
          margin: "0 auto",
          fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
          color: "#222",
          paddingBottom: "40px",
          paddingLeft: "10px",
          paddingRight: "10px",
        }}
      >
        <h1
          style={{
            textAlign: "center",
            paddingTop: "30px",
            paddingBottom: "20px",
            color: "#0056b3",
            fontWeight: "900",
            fontSize: "44px",
            letterSpacing: "1.1px",
          }}
        >
          Cybersecurity Dashboard
        </h1>
        <Navigation />
        <Routes>
          <Route path="/" element={<Navigate replace to="/news" />} />
          <Route path="/news" element={<News />} />
          <Route path="/vulnerabilities" element={<Vulnerabilities />} />
        </Routes>
      </div>
    </Router>
  );
}
