import React from "react";
import { BrowserRouter as Router, Routes, Route, Link, Navigate, useLocation } from "react-router-dom";
import GoogleAd from "./GoogleAd";

// Environment variable URLs
const BACKEND_PROXY_URL = process.env.REACT_APP_BACKEND_PROXY_URL || "https://newsd.onrender.com/api/vulnerabilities";
const BACKEND_NEWS_URL = process.env.REACT_APP_BACKEND_NEWS_URL || "https://newsd.onrender.com/api/news";

const colors = {
  background: "#121212",
  surface: "#1E1E1E",
  primary: "#90caf9",
  primaryDark: "#64b5f6",
  textPrimary: "#FFFFFF",
  textSecondary: "#B0BEC5",
  error: "#cf6679",
  border: "#333",
};

const cardStyle = {
  borderRadius: 12,
  boxShadow: "0 4px 10px rgba(0,0,0,0.7)",
  backgroundColor: colors.surface,
  padding: 20,
  marginBottom: 30,
  color: colors.textPrimary,
  textDecoration: "none",
  boxSizing: "border-box",
  maxWidth: "100%", // 100% for responsiveness
};

// News filtering for true cybersecurity content
function isCybersecurityArticle(article) {
  const fields = [article.title, article.description, article.content].join(" ").toLowerCase();
  return (
    fields.includes("cyber") ||
    fields.includes("security") ||
    fields.includes("hacking") ||
    fields.includes("breach") ||
    fields.includes("vulnera") ||
    fields.includes("infosec")
  );
}

function News() {
  const [newsList, setNewsList] = React.useState([]);
  const [loading, setLoading] = React.useState(false);
  const [error, setError] = React.useState(null);

  React.useEffect(() => {
    async function fetchNews() {
      setLoading(true);
      setError(null);
      try {
        const response = await fetch(BACKEND_NEWS_URL);
        if (!response.ok) throw new Error("Failed to fetch news");
        const data = await response.json();
        setNewsList(data.articles || []);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }
    fetchNews();
  }, []);

  const filteredNews = newsList.filter(isCybersecurityArticle);
  const halfway = Math.ceil(filteredNews.length / 2);

  return (
    <div style={{ padding: 20, backgroundColor: colors.background, minHeight: "80vh", maxWidth: 960, margin: "auto" }}>
      <h2 style={{ fontSize: 28, fontWeight: 700, marginBottom: 30, color: colors.primary, textAlign: "center" }}>
        Latest Cybersecurity News
      </h2>

      {loading && <p style={{ fontSize: 18, color: colors.textSecondary }}>Loading news...</p>}
      {error && <p style={{ color: colors.error, fontSize: 18 }}>{error}</p>}
      {!loading && filteredNews.length === 0 && (
        <p style={{ fontSize: 18, color: colors.textSecondary, textAlign: "center" }}>No cybersecurity news found.</p>
      )}

      {filteredNews.slice(0, halfway).map((news, i) => (
        <NewsCard key={i} news={news} />
      ))}

      {/* Insert AdSense Ad after first half of news */}
      {filteredNews.length > 0 && (
        <div style={{ margin: "30px 0" }}>
          <GoogleAd />
        </div>
      )}

      {filteredNews.slice(halfway).map((news, i) => (
        <NewsCard key={i + halfway} news={news} />
      ))}
    </div>
  );
}

function NewsCard({ news }) {
  return (
    <div
      style={{
        marginBottom: 20,
        borderBottom: `1px solid ${colors.border}`,
        paddingBottom: 15,
        display: "flex",
        gap: 15,
        flexWrap: "wrap",
        alignItems: "center",
      }}
    >
      <img
        src={news.urlToImage || "/cyber-placeholder.jpg"}
        onError={(e) => {
          e.target.onerror = null;
          e.target.src = "/cyber-placeholder.jpg";
        }}
        alt="news"
        style={{ width: "100%", maxWidth: 300, height: 160, objectFit: "cover", borderRadius: 6, filter: "brightness(0.9)" }}
        loading="lazy"
      />
      <div style={{ flex: "1 1 300px", color: colors.textPrimary }}>
        <h3 style={{ fontSize: 20, marginBottom: 12, lineHeight: 1.3 }}>{news.title}</h3>
        <p style={{ fontSize: 14, marginBottom: 12, color: colors.textSecondary }}>
          {news.description || news.content}
        </p>
        <p style={{ fontSize: 12, color: colors.textSecondary, fontStyle: "italic" }}>
          {news.source?.name} | {new Date(news.publishedAt).toLocaleString()}
        </p>
      </div>
    </div>
  );
}

function Vulnerabilities() {
  const [packageName, setPackageName] = React.useState("jinja2");
  const [version, setVersion] = React.useState("2.4.1");
  const [ecosystem, setEcosystem] = React.useState("PyPI");
  const [company] = React.useState("");
  const [severityFilter] = React.useState("");

  const [vulnerabilities, setVulnerabilities] = React.useState([]);
  const [loading, setLoading] = React.useState(false);
  const [error, setError] = React.useState(null);

  React.useEffect(() => {
    async function fetchVulnerabilities() {
      setLoading(true);
      setError(null);
      try {
        const response = await fetch(BACKEND_PROXY_URL, {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ packageName, version, ecosystem, company, severityFilter }),
        });
        if (!response.ok) throw new Error("Failed to fetch vulnerabilities");
        const data = await response.json();
        setVulnerabilities(data.vulns || []);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }
    fetchVulnerabilities();
  }, [packageName, version, ecosystem, company, severityFilter]);

  // Truncate utility for text overflow
  const truncateText = (text, maxLength = 100) => {
    if (!text) return "";
    return text.length > maxLength ? text.slice(0, maxLength) + "…" : text;
  };

  // ... [parseCvssSeverity and other helper functions unchanged] ...

  const notesStyle = {
    flex: "0 0 300px",
    maxWidth: 300,
    minWidth: 240,
    marginTop: 200,
    top: 24,
    marginLeft: "auto",
    backgroundColor: colors.surface,
    borderRadius: 8,
    padding: 20,
    fontSize: 14,
    color: colors.textSecondary,
    lineHeight: 1.5,
    height: "fit-content",
    boxSizing: "border-box",
    display: "none", // Hide on small screens by default
  };

  return (
    <div
      style={{
        display: "flex",
        alignItems: "flex-start",
        gap: 40,
        padding: "20px 10px",
        flexWrap: "wrap",
        justifyContent: "flex-start",
        backgroundColor: colors.background,
        minHeight: "80vh",
        maxWidth: 960,
        margin: "auto",
      }}
    >
      <main
        style={{
          flex: "1 1 500px",
          maxWidth: "100%",
          minWidth: 320,
        }}
      >
        <div
          className="center"
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            height: "100%",
            minHeight: 200,
            width: "100%",
          }}
        >
          <h2 style={{ fontSize: 24, marginBottom: 20, color: colors.primary, textAlign: "center" }}>
            Vulnerabilities for {packageName} version {version} ({ecosystem})
          </h2>

          <div style={{ display: "flex", justifyContent: "center", marginBottom: 20, width: "100%", flexWrap: "wrap", gap: 10 }}>
            <input
              type="text"
              value={packageName}
              onChange={(e) => setPackageName(e.target.value)}
              placeholder="Package Name"
              required
              style={{ flexBasis: "45%", padding: 8, borderRadius: 4, border: "1px solid #ccc", minWidth: 150 }}
            />
            <input
              type="text"
              value={version}
              onChange={(e) => setVersion(e.target.value)}
              placeholder="Version"
              required
              style={{ flexBasis: "25%", padding: 8, borderRadius: 4, border: "1px solid #ccc", minWidth: 100 }}
            />
            <input
              type="text"
              value={ecosystem}
              onChange={(e) => setEcosystem(e.target.value)}
              placeholder="Ecosystem"
              required
              style={{ flexBasis: "25%", padding: 8, borderRadius: 4, border: "1px solid #ccc", minWidth: 120 }}
            />
          </div>

          <div style={{ display: "flex", justifyContent: "center", marginBottom: 30, width: "100%", flexWrap: "wrap" }}>
            <button
              onClick={() => {
                // re-fetch vulnerabilities with current input states
                (async () => {
                  setLoading(true);
                  setError(null);
                  try {
                    const response = await fetch(BACKEND_PROXY_URL, {
                      method: "POST",
                      headers: { "Content-Type": "application/json" },
                      body: JSON.stringify({ packageName, version, ecosystem, company, severityFilter }),
                    });
                    if (!response.ok) throw new Error("Failed to fetch vulnerabilities");
                    const data = await response.json();
                    setVulnerabilities(data.vulns || []);
                  } catch (err) {
                    setError(err.message);
                  } finally {
                    setLoading(false);
                  }
                })();
              }}
              style={{
                flexBasis: "100%",
                maxWidth: 150,
                backgroundColor: colors.primary,
                color: colors.textPrimary,
                border: "none",
                borderRadius: 4,
                cursor: "pointer",
                fontWeight: "bold",
                fontSize: 16,
                padding: 10,
                userSelect: "none",
                transition: "background-color 0.2s ease",
              }}
              onMouseEnter={(e) => (e.currentTarget.style.backgroundColor = colors.primaryDark)}
              onMouseLeave={(e) => (e.currentTarget.style.backgroundColor = colors.primary)}
            >
              Search
            </button>
          </div>

          {/* Responsive Ad below the search form */}
          <div style={{ marginBottom: 30 }}>
            <GoogleAd />
          </div>
        </div>

        {loading && <p style={{ color: colors.textSecondary }}>Loading vulnerabilities...</p>}
        {error && <p style={{ color: colors.error }}>{error}</p>}
        {!loading && vulnerabilities.length === 0 && (
          <p style={{ color: colors.textSecondary }}>No vulnerabilities found.</p>
        )}

        <div style={{ display: "flex", flexDirection: "column", gap: 20 }}>
          {vulnerabilities.map((vuln) => {
            const cvssSummary = parseCvssSeverity(vuln.severity);

            return (
              <article
                key={vuln.id}
                style={{
                  ...cardStyle,
                  backgroundColor: colors.surface,
                  color: colors.textPrimary,
                }}
              >
                <h3 style={{ margin: 0, marginBottom: 10, color: colors.primary }}>
                  <a
                    href={`https://osv.dev/vulnerability/${vuln.id}`}
                    target="_blank"
                    rel="noopener noreferrer"
                    style={{ textDecoration: "none", color: colors.primary }}
                  >
                    {vuln.id}
                  </a>
                </h3>
                <p style={{ fontSize: 16, marginBottom: 8 }}>
                  <strong>Description:</strong> {truncateText(vuln.summary || "No summary available.")}
                </p>
                <p
                  style={{
                    whiteSpace: "pre-line",
                    fontSize: 14,
                    color: colors.textSecondary,
                    marginBottom: 8,
                    fontStyle: "italic",
                  }}
                >
                  <strong>Impact Details:</strong>
                  {"\n" + (cvssSummary || "Not available")}
                </p>
                {vuln.published && (
                  <p style={{ fontSize: 16, marginBottom: 8 }}>
                    <strong>Published Date:</strong> {new Date(vuln.published).toLocaleDateString()}
                  </p>
                )}
                {vuln.references && vuln.references.length > 0 && (
                  <div style={{ fontSize: 16 }}>
                    <strong>References:</strong>
                    <ul style={{ margin: "8px 0 0 20px", wordBreak: "break-word" }}>
                      {vuln.references.map((ref, i) => {
                        const displayedUrl = ref.url.length > 60 ? ref.url.slice(0, 157) + "…" : ref.url;
                        return (
                          <li key={i}>
                            <a href={ref.url} target="_blank" rel="noopener noreferrer" title={ref.url} style={{ color: colors.primary }}>
                              {displayedUrl}
                            </a>
                          </li>
                        );
                      })}
                    </ul>
                  </div>
                )}
              </article>
            );
          })}
        </div>
      </main>

      <aside style={{ ...notesStyle, display: window.innerWidth >= 768 ? "block" : "none" }}>
        <GoogleAd />
        <h3 style={{ color: colors.primary, marginTop: 0, marginBottom: 12 }}>How to Read Vulnerabilities</h3>
        <p>
          <strong>Vulnerability ID:</strong> Unique ID for this vulnerability (e.g., GHSA-462w-v97r-4m45).
        </p>
        <p><strong>Description:</strong> A summary explaining what this vulnerability is and how it affects software.</p>
        <p>
          <strong>Impact Details:</strong> Detailed info about attack vector, complexity, privileges, user interaction, scope, and impact on confidentiality, integrity,
          and availability.
        </p>
        <p><strong>Published Date:</strong> When this vulnerability was officially disclosed.</p>
        <p><strong>References:</strong> External, trusted links providing more information or patches.</p>
        <p>This dashboard helps you assess software risk and decide on updates or patches.</p>
      </aside>
    </div>
  );
}

function Navigation() {
  const location = useLocation();
  const linkStyle = {
    marginRight: 20,
    textDecoration: "none",
    fontWeight: "700",
    fontSize: 18,
    color: colors.textPrimary,
  };
  const activeStyle = {
    color: colors.primary,
    borderBottom: `3px solid ${colors.primary}`,
    paddingBottom: 4,
    transition: "all 0.3s ease",
  };

  return (
    <nav
      style={{
        padding: "20px 40px",
        borderBottom: `2px solid ${colors.border}`,
        marginBottom: 40,
        backgroundColor: colors.surface,
        fontWeight: 600,
        fontSize: 20,
        overflowX: "auto",
      }}
    >
      <Link to="/news" style={{ ...linkStyle, ...(location.pathname === "/news" ? activeStyle : {}) }}>
        News
      </Link>
      <Link to="/vulnerabilities" style={{ ...linkStyle, ...(location.pathname === "/vulnerabilities" ? activeStyle : {}) }}>
        Vulnerabilities
      </Link>
    </nav>
  );
}

export default function App() {
  return (
    <Router>
      <div
        style={{
          maxWidth: 960,
          margin: "0 auto",
          fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
          color: colors.textPrimary,
          backgroundColor: colors.background,
          minHeight: "100vh",
          paddingBottom: 40,
          paddingLeft: 16,
          paddingRight: 16,
        }}
      >
        <h1
          style={{
            textAlign: "center",
            paddingTop: 30,
            paddingBottom: 20,
            color: colors.primary,
            fontWeight: 900,
            fontSize: 40,
            letterSpacing: 1.1,
          }}
        >
          Spider Dashboard
        </h1>
        <Navigation />
        <Routes>
          <Route path="/" element={<Navigate replace to="/news" />} />
          <Route path="/news" element={<News />} />
          <Route path="/vulnerabilities" element={<Vulnerabilities />} />
        </Routes>
      </div>
    </Router>
  );
}
