import React, { useEffect, useState } from "react";

const NEWS_API_KEY = "9ec3cd1c0603415fa22623a3313343bf"; // Replace with your NewsAPI key
const NEWS_API_URL = `https://newsapi.org/v2/everything?q=cyber%20security&language=en&sortBy=publishedAt&apiKey=${NEWS_API_KEY}`;

// NVD API endpoint for recent CVEs (here using a public feed URL as example)
const NVD_API_URL =
  "https://services.nvd.nist.gov/rest/json/cves/1.0?resultsPerPage=10&startIndex=0&pubStartDate=2025-01-01T00:00:00:000 UTC-00:00";

function formatCveDate(dateStr) {
  return new Date(dateStr).toLocaleDateString();
}

function extractCvssInfo(metrics) {
  if (!metrics) return {};

  // Get CVSS v3 or fallback to v2
  const cvssV3 = metrics["cvssV3"];
  const cvssV2 = metrics["cvssV2"];

  if (cvssV3) {
    return {
      score: cvssV3.baseScore,
      vector: cvssV3.vectorString,
      severity: cvssV3.baseSeverity,
      attackVector: cvssV3.attackVector,
      attackComplexity: cvssV3.attackComplexity,
      privilegesRequired: cvssV3.privilegesRequired,
      userInteraction: cvssV3.userInteraction,
      scope: cvssV3.scope,
      confidentialityImpact: cvssV3.confidentialityImpact,
      integrityImpact: cvssV3.integrityImpact,
      availabilityImpact: cvssV3.availabilityImpact,
    };
  } else if (cvssV2) {
    return {
      score: cvssV2.baseScore,
      vector: cvssV2.vectorString,
      severity: cvssV2.severity,
      attackVector: cvssV2.accessVector,
      accessComplexity: cvssV2.accessComplexity,
      authentication: cvssV2.authentication,
      confidentialityImpact: cvssV2.confidentialityImpact,
      integrityImpact: cvssV2.integrityImpact,
      availabilityImpact: cvssV2.availabilityImpact,
    };
  }
  return {};
}

function CyberSecurityDashboard() {
  const [newsList, setNewsList] = useState([]);
  const [vulnerabilities, setVulnerabilities] = useState([]);
  const [loadingNews, setLoadingNews] = useState(false);
  const [loadingVuln, setLoadingVuln] = useState(false);
  const [errorNews, setErrorNews] = useState(null);
  const [errorVuln, setErrorVuln] = useState(null);

  useEffect(() => {
    async function fetchNews() {
      setLoadingNews(true);
      setErrorNews(null);
      try {
        const res = await fetch(NEWS_API_URL);
        if (!res.ok) throw new Error(`NewsAPI error: ${res.status}`);
        const data = await res.json();
        setNewsList(data.articles || []);
      } catch (err) {
        setErrorNews(err.message);
      } finally {
        setLoadingNews(false);
      }
    }

    async function fetchVulnerabilities() {
      setLoadingVuln(true);
      setErrorVuln(null);
      try {
        const res = await fetch(NVD_API_URL, {
          headers: {
            // If API key needed: 'API_KEY': 'your_key_here'
          },
        });
        if (!res.ok) throw new Error(`NVD API error: ${res.status}`);
        const data = await res.json();
        setVulnerabilities(data.result?.CVE_Items || []);
      } catch (err) {
        setErrorVuln(err.message);
      } finally {
        setLoadingVuln(false);
      }
    }

    fetchNews();
    fetchVulnerabilities();
  }, []);

  return (
    <div style={{ maxWidth: "900px", margin: "0 auto", fontFamily: "Arial, sans-serif", padding: "20px" }}>
      <h1>Cyber Security Dashboard</h1>

      <section>
        <h2>Latest Cyber Security News</h2>
        {loadingNews && <p>Loading news...</p>}
        {errorNews && <p style={{ color: "red" }}>{errorNews}</p>}
        {!loadingNews && newsList.length === 0 && <p>No news found.</p>}
        {newsList.map((news, i) => (
          <div key={i} style={{ marginBottom: "20px", borderBottom: "1px solid #ccc", paddingBottom: "15px" }}>
            <h3>{news.title}</h3>
            <p>
              <strong>Source:</strong> {news.source?.name || "Unknown"} |{" "}
              <strong>Published:</strong> {new Date(news.publishedAt).toLocaleString()}
            </p>
            {news.urlToImage && (
              <img
                src={news.urlToImage}
                alt="news"
                style={{ maxWidth: "100%", height: "auto", marginBottom: "10px" }}
              />
            )}
            <p>{news.description || news.content}</p>
            <a href={news.url} target="_blank" rel="noopener noreferrer">
              Read full article
            </a>
          </div>
        ))}
      </section>

      <section style={{ marginTop: "40px" }}>
        <h2>Recently Discovered Vulnerabilities</h2>
        {loadingVuln && <p>Loading vulnerabilities...</p>}
        {errorVuln && <p style={{ color: "red" }}>{errorVuln}</p>}
        {!loadingVuln && vulnerabilities.length === 0 && <p>No vulnerabilities found.</p>}

        {vulnerabilities.map((item) => {
          const cve = item.cve;
          const cveId = cve.CVE_data_meta.ID;
          const description = cve.description.description_data[0]?.value;
          const metrics = item.impact?.baseMetricV3 || item.impact?.baseMetricV2;
          const cvssInfo = extractCvssInfo(metrics);

          return (
            <div
              key={cveId}
              style={{ marginBottom: "20px", borderBottom: "1px solid #ccc", paddingBottom: "15px" }}
            >
              <h3>
                <a
                  href={`https://nvd.nist.gov/vuln/detail/${cveId}`}
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  {cveId}
                </a>
              </h3>
              <p>{description}</p>

              <table style={{ width: "100%", borderCollapse: "collapse", marginTop: "8px" }}>
                <tbody>
                  <tr>
                    <td><strong>Vulnerability Score:</strong></td>
                    <td>{cvssInfo.score || "N/A"}</td>
                  </tr>
                  {cvssInfo.severity && (
                    <tr>
                      <td><strong>Severity:</strong></td>
                      <td>{cvssInfo.severity}</td>
                    </tr>
                  )}
                  {cvssInfo.attackVector && (
                    <tr>
                      <td><strong>Attack Vector:</strong></td>
                      <td>{cvssInfo.attackVector}</td>
                    </tr>
                  )}
                  {cvssInfo.attackComplexity && (
                    <tr>
                      <td><strong>Attack Complexity:</strong></td>
                      <td>{cvssInfo.attackComplexity}</td>
                    </tr>
                  )}
                  {cvssInfo.privilegesRequired && (
                    <tr>
                      <td><strong>Privileges Required:</strong></td>
                      <td>{cvssInfo.privilegesRequired}</td>
                    </tr>
                  )}
                  {cvssInfo.userInteraction && (
                    <tr>
                      <td><strong>User Interaction:</strong></td>
                      <td>{cvssInfo.userInteraction}</td>
                    </tr>
                  )}
                  {cvssInfo.confidentialityImpact && (
                    <tr>
                      <td><strong>Confidentiality Impact:</strong></td>
                      <td>{cvssInfo.confidentialityImpact}</td>
                    </tr>
                  )}
                  {cvssInfo.integrityImpact && (
                    <tr>
                      <td><strong>Integrity Impact:</strong></td>
                      <td>{cvssInfo.integrityImpact}</td>
                    </tr>
                  )}
                  {cvssInfo.availabilityImpact && (
                    <tr>
                      <td><strong>Availability Impact:</strong></td>
                      <td>{cvssInfo.availabilityImpact}</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          );
        })}
      </section>
    </div>
  );
}

export default CyberSecurityDashboard;
