// Components/NewsList.js
import React, { useEffect, useState } from "react";
import axios from "axios";

function NewsList({ apiUrl, itemType }) {
  const [articles, setArticles] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const options = {
          method: "GET",
          url: apiUrl || "https://cybersecurity-news.p.rapidapi.com/news/guardian",
          headers: {
            "X-RapidAPI-Key": "YOUR_API_KEY_HERE",
            "X-RapidAPI-Host": "cybersecurity-news.p.rapidapi.com"
          }
        };

        const res = await axios.request(options);
        console.log("API Response:", res.data);

        // Adjust data extraction based on actual response structure
        if (Array.isArray(res.data)) {
          setArticles(res.data);
        } else if (res.data.articles) {
          setArticles(res.data.articles);
        } else {
          console.warn("Unexpected response format:", res.data);
        }
      } catch (err) {
        console.error("Error fetching news:", err.response?.data || err.message);
      }
    };

    fetchData();
  }, [apiUrl]);

  return (
    <div style={{ padding: "20px" }}>
      <h2>{itemType ? itemType : "Cybersecurity News"}</h2>
      {articles.length > 0 ? (
        <ul>
          {articles.map((article, index) => (
            <li key={index}>
              <a href={article.link || article.url} target="_blank" rel="noopener noreferrer">
                {article.title}
              </a>
            </li>
          ))}
        </ul>
      ) : (
        <p>No articles found.</p>
      )}
    </div>
  );
}

export default NewsList;
