import React from "react";
import { BrowserRouter as Router, Routes, Route, Link, Navigate, useLocation } from "react-router-dom";

const NEWS_API_KEY = "9ec3cd1c0603415fa22623a3313343bf";
const NEWS_API_URL =
  `https://newsapi.org/v2/everything?` +
  `q=cybersecurity OR "cyber security" OR hacking OR "data breach" OR "information security"&` +
  `language=en&sortBy=publishedAt&apiKey=${NEWS_API_KEY}`;

// Change to your deployed backend URL when deploying
const BACKEND_PROXY_URL = "http://localhost:4000/api/vulnerabilities";

const cardStyle = {
  borderRadius: "12px",
  boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
  backgroundColor: "#fff",
  transition: "transform 0.2s ease, box-shadow 0.2s ease",
  cursor: "pointer",
  padding: "20px",
  marginBottom: "30px",
  color: "#222",
  textDecoration: "none",
  boxSizing: "border-box",
};

const cardHoverStyle = {
  transform: "translateY(-5px)",
  boxShadow: "0 8px 20px rgba(0,0,0,0.15)",
};

function News() {
  const [newsList, setNewsList] = React.useState([]);
  const [loading, setLoading] = React.useState(false);
  const [error, setError] = React.useState(null);

  const getRandomCybersecurityImage = () => {
    return `https://source.unsplash.com/random/600x460/?cybersecurity,technology,computer,security,hacking`;
  };

  React.useEffect(() => {
    async function fetchNews() {
      setLoading(true);
      setError(null);
      try {
        const response = await fetch(NEWS_API_URL);
        if (!response.ok) throw new Error("Failed to fetch news");
        const data = await response.json();
        setNewsList(data.articles || []);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }
    fetchNews();
  }, []);

  return (
    <div style={{ padding: "20px", backgroundColor: "#f9f9f9" }}>
      <h2 style={{ fontSize: "32px", fontWeight: "700", marginBottom: "30px", color: "#0056b3" }}>
        Latest Cybersecurity News
      </h2>
      {loading && <p style={{ fontSize: "18px" }}>Loading news...</p>}
      {error && <p style={{ color: "red", fontSize: "18px" }}>{error}</p>}
      {!loading && newsList.length === 0 && <p style={{ fontSize: "18px" }}>No news articles found.</p>}

      {newsList.map((news, i) => (
        <div
          key={i}
          style={{
            marginBottom: "20px",
            borderBottom: "1px solid #ddd",
            paddingBottom: "15px",
            display: "flex",
            gap: "15px",
            flexWrap: "wrap",
          }}
        >
          <img
            src={news.urlToImage || getRandomCybersecurityImage()}
            alt="news"
            style={{ width: "100%", maxWidth: 300, height: 160, objectFit: "cover", borderRadius: "6px" }}
            loading="lazy"
          />
          <div style={{ flex: "1 1 300px" }}>
            <h3 style={{ fontSize: "24px", marginBottom: "12px", color: "#004080", lineHeight: 1.2 }}>
              {news.title}
            </h3>
            <p style={{ fontSize: "16px", color: "#222", marginBottom: "12px" }}>
              {news.description || news.content}
            </p>
            <p style={{ fontSize: "14px", color: "#666", fontStyle: "italic" }}>
              {news.source?.name} | {new Date(news.publishedAt).toLocaleString()}
            </p>
          </div>
        </div>
      ))}
    </div>
  );
}

function Vulnerabilities() {
  const [packageName, setPackageName] = React.useState("jinja2");
  const [version, setVersion] = React.useState("2.4.1");
  const [ecosystem, setEcosystem] = React.useState("PyPI");

  const [vulnerabilities, setVulnerabilities] = React.useState([]);
  const [loading, setLoading] = React.useState(false);
  const [error, setError] = React.useState(null);

  React.useEffect(() => {
    async function fetchVulnerabilities() {
      setLoading(true);
      setError(null);
      try {
        const response = await fetch(BACKEND_PROXY_URL, {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ packageName, version, ecosystem }),
        });
        if (!response.ok) throw new Error("Failed to fetch vulnerabilities");
        const data = await response.json();
        setVulnerabilities(data.vulns || []);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }
    fetchVulnerabilities();
  }, [packageName, version, ecosystem]);

  // Helper to safely convert severity field to string for rendering
  const getSeverityText = (severity) => {
    if (!severity) return "Unknown";
    if (typeof severity === "string") return severity;
    if (typeof severity === "object") {
      if ("score" in severity) return severity.score;
      if ("type" in severity) return severity.type;
      return JSON.stringify(severity);
    }
    return String(severity);
  };

  const notesStyle = {
    flex: "0 0 300px",
    maxWidth: "300px",
    minWidth: "240px",
    position: "sticky",
    top: 24,
    marginLeft: "auto",
    backgroundColor: "#f0f8ff",
    borderRadius: 8,
    padding: 20,
    fontSize: 14,
    color: "#333",
    lineHeight: 1.5,
    height: "fit-content",
    boxSizing: "border-box",
  };

  return (
    <div
      style={{
        display: "flex",
        alignItems: "flex-start",
        gap: 40,
        padding: "20px 10px",
        flexWrap: "wrap",
        justifyContent: "flex-start",
      }}
    >
      <main
        style={{
          flex: "1 1 500px",
          maxWidth: "700px",
          minWidth: "320px",
        }}
      >
        <h2 style={{ fontSize: "28px", marginBottom: 20, color: "#0056b3" }}>
          Vulnerabilities for {packageName} version {version} ({ecosystem})
        </h2>

        <form
          onSubmit={(e) => {
            e.preventDefault();
            // fetching already handled on state change
          }}
          style={{ marginBottom: 30, display: "flex", gap: 10, flexWrap: "wrap" }}
        >
          <input
            type="text"
            value={packageName}
            onChange={(e) => setPackageName(e.target.value)}
            placeholder="Package Name"
            required
            style={{ flex: "1 1 150px", padding: 8, borderRadius: 4, border: "1px solid #ccc" }}
          />
          <input
            type="text"
            value={version}
            onChange={(e) => setVersion(e.target.value)}
            placeholder="Version"
            required
            style={{ flex: "1 1 100px", padding: 8, borderRadius: 4, border: "1px solid #ccc" }}
          />
          <input
            type="text"
            value={ecosystem}
            onChange={(e) => setEcosystem(e.target.value)}
            placeholder="Ecosystem"
            required
            style={{ flex: "1 1 120px", padding: 8, borderRadius: 4, border: "1px solid #ccc" }}
          />
          <button
            type="submit"
            style={{
              flex: "1 1 120px",
              backgroundColor: "#0056b3",
              color: "#fff",
              border: "none",
              borderRadius: 4,
              cursor: "pointer",
              fontWeight: "bold",
            }}
          >
            Search
          </button>
        </form>

        {loading && <p>Loading vulnerabilities...</p>}
        {error && <p style={{ color: "red" }}>{error}</p>}
        {!loading && vulnerabilities.length === 0 && <p>No vulnerabilities found.</p>}

        <div style={{ display: "flex", flexDirection: "column", gap: 20 }}>
          {vulnerabilities.map((vuln) => (
            <article
              key={vuln.id}
              style={{
                border: "1px solid #ddd",
                borderRadius: 8,
                padding: 16,
                backgroundColor: "#fff",
              }}
            >
              <h3 style={{ margin: 0, marginBottom: 10, color: "#004080" }}>
                <a
                  href={`https://osv.dev/vulnerability/${vuln.id}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  style={{ textDecoration: "none", color: "#007acc" }}
                >
                  {vuln.id}
                </a>
              </h3>
              <p style={{ fontSize: 16, marginBottom: 8 }}>
                <strong>Description:</strong> {vuln.summary || "No summary available."}
              </p>
              <p style={{ fontSize: 16, marginBottom: 8 }}>
                <strong>Severity:</strong> {getSeverityText(vuln.severity)}
              </p>
              {vuln.published && (
                <p style={{ fontSize: 16, marginBottom: 8 }}>
                  <strong>Published Date:</strong> {new Date(vuln.published).toLocaleDateString()}
                </p>
              )}
              {vuln.references && vuln.references.length > 0 && (
                <div style={{ fontSize: 16 }}>
                  <strong>References:</strong>
                  <ul style={{ margin: "8px 0 0 20px" }}>
                    {vuln.references.map((ref, i) => (
                      <li key={i}>
                        <a href={ref.url} target="_blank" rel="noopener noreferrer">
                          {ref.url}
                        </a>
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </article>
          ))}
        </div>
      </main>

      <aside style={notesStyle}>
        <h3 style={{ marginTop: 0, marginBottom: 12 }}>How to Read Vulnerabilities</h3>
        <p>
          <strong>Vulnerability ID:</strong> Unique identifier to reference this vulnerability.
        </p>
        <p>
          <strong>Description:</strong> Short summary explaining the vulnerability and its impact.
        </p>
        <p>
          <strong>Severity:</strong> Indicates risk level (Critical, High, Medium, Low, Unknown).
        </p>
        <p>
          <strong>Published Date:</strong> The date when the vulnerability was publicly reported.
        </p>
        <p>
          <strong>References:</strong> Links to external resources providing more information.
        </p>
        <p>
          <strong>Package:</strong> The software library or component affected.
        </p>
        <p>
          <strong>Version:</strong> The specific release version of the package potentially impacted.
        </p>
        <p>
          <strong>Ecosystem:</strong> The package ecosystem or registry (e.g., PyPI, npm, Maven).
        </p>
        <p>
          This dashboard helps you identify known security issues in software packages so you can decide if updates or patches are needed.
        </p>
      </aside>
    </div>
  );
}

function Navigation() {
  const location = useLocation();
  const linkStyle = { marginRight: 30, textDecoration: "none", fontWeight: "700", fontSize: "18px" };
  const activeStyle = {
    color: "#007acc",
    borderBottom: "3px solid #007acc",
    paddingBottom: "4px",
    transition: "all 0.3s ease",
  };

  return (
    <nav
      style={{
        padding: "20px 40px",
        borderBottom: "2px solid #ccc",
        marginBottom: "40px",
        backgroundColor: "#f0f8ff",
        fontWeight: "600",
        fontSize: "20px",
      }}
    >
      <Link to="/news" style={{ ...linkStyle, ...(location.pathname === "/news" ? activeStyle : {}) }}>
        News
      </Link>
      <Link to="/vulnerabilities" style={{ ...linkStyle, ...(location.pathname === "/vulnerabilities" ? activeStyle : {}) }}>
        Vulnerabilities
      </Link>
    </nav>
  );
}

const notesStyle = {
  flex: "0 0 300px",
  maxWidth: "300px",
  minWidth: "240px",
  position: "sticky",
  top: 24,
  marginLeft: "auto",
  backgroundColor: "#f0f8ff",
  borderRadius: 8,
  padding: 20,
  fontSize: 14,
  color: "#333",
  lineHeight: 1.5,
  height: "fit-content",
  boxSizing: "border-box",
};

export default function App() {
  return (
    <Router>
      <div
        style={{
          maxWidth: 1024,
          margin: "0 auto",
          fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
          color: "#222",
          paddingBottom: "40px",
          paddingLeft: "10px",
          paddingRight: "10px",
        }}
      >
        <h1
          style={{
            textAlign: "center",
            paddingTop: "30px",
            paddingBottom: "20px",
            color: "#0056b3",
            fontWeight: "900",
            fontSize: "44px",
            letterSpacing: "1.1px",
          }}
        >
          Cybersecurity Dashboard
        </h1>
        <Navigation />
        <Routes>
          <Route path="/" element={<Navigate replace to="/news" />} />
          <Route path="/news" element={<News />} />
          <Route path="/vulnerabilities" element={<Vulnerabilities />} />
        </Routes>
      </div>
    </Router>
  );
}
