import React, { useState, useEffect } from "react";

const API_KEY = "9ec3cd1c0603415fa22623a3313343bf"; // Replace with your NewsAPI key
const NEWS_API_URL = `https://newsapi.org/v2/everything?q=cyber%20security&language=en&sortBy=publishedAt&apiKey=${API_KEY}`;

function CyberNewsApp() {
  const [newsList, setNewsList] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function fetchNews() {
      setLoading(true);
      setError(null);
      try {
        const response = await fetch(NEWS_API_URL);
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        const data = await response.json();
        setNewsList(data.articles || []);
      } catch (err) {
        setError("Failed to load cyber security news.");
      } finally {
        setLoading(false);
      }
    }
    fetchNews();
  }, []);

  return (
    <div style={{ maxWidth: "900px", margin: "20px auto", fontFamily: "Arial, sans-serif" }}>
      <h1>Latest Cyber Security News</h1>

      {loading && <p>Loading news...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}

      {!loading && newsList.length === 0 && <p>No news articles found.</p>}

      <div>
        {newsList.map((news, idx) => (
          <div key={idx} style={{ borderBottom: "1px solid #ccc", paddingBottom: "15px", marginBottom: "15px" }}>
            <h3>{news.title}</h3>
            <p>
              <strong>Source:</strong> {news.source?.name || "Unknown"} |{" "}
              <strong>Published:</strong> {new Date(news.publishedAt).toLocaleString()}
            </p>
            {news.urlToImage && (
              <img src={news.urlToImage} alt="news" style={{ maxWidth: "100%", height: "auto", marginBottom: "10px" }} />
            )}
            <p>{news.description || news.content}</p>
            <a href={news.url} target="_blank" rel="noopener noreferrer">
              Read full article
            </a>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CyberNewsApp;
