import React, { useState, useEffect } from "react";

// API endpoints assumed for demonstration purposes:
// GET /api/news-types -> returns array of news types (e.g. ["general", "dataBreach", ...])
// GET /api/news?type={newsType} -> returns news articles for a type

function CyberNewsApp() {
  const [newsTypes, setNewsTypes] = useState([]);
  const [selectedType, setSelectedType] = useState("");
  const [newsList, setNewsList] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Example mock data
const mockNewsTypes = ["general", "dataBreach"];
const mockNewsList = [
  {
    id: 1,
    headlines: "Sample news headline",
    author: "Author Name",
    newsDate: "Aug 31 2025",
    newsImgURL: "https://example.com/image.jpg",
    fullNews: "Detailed news content here.",
    newsURL: "https://example.com/news-article"
  }
];

  // Fetch news types on component mount
  useEffect(() => {
    async function fetchNewsTypes() {
      try {
        const res = await fetch("/api/news-types");
        const data = await res.json();
        setNewsTypes(data);
      } catch (err) {
        setError("Failed to load news types.");
      }
    }
    fetchNewsTypes();
  }, []);

  // Fetch news for selected type
  useEffect(() => {
    if (!selectedType) return;
    setLoading(true);
    setError(null);
    async function fetchNews() {
      try {
        const res = await fetch(`/api/news?type=${encodeURIComponent(selectedType)}`);
        const data = await res.json();
        setNewsList(data);
      } catch (err) {
        setError("Failed to load news.");
      } finally {
        setLoading(false);
      }
    }
    fetchNews();
  }, [selectedType]);

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      <h1>Cyber Security News</h1>

      <div>
        <label htmlFor="newsTypeSelect">Select News Type: </label>
        <select
          id="newsTypeSelect"
          value={selectedType}
          onChange={(e) => setSelectedType(e.target.value)}
        >
          <option value="">-- Choose --</option>
          {newsTypes.map((type) => (
            <option key={type} value={type}>
              {type}
            </option>
          ))}
        </select>
      </div>

      {loading && <p>Loading news...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}

      <div style={{ marginTop: "20px" }}>
        {newsList.length === 0 && !loading && selectedType && <p>No news found.</p>}

        {newsList.map((news) => (
          <div key={news.id} style={{ borderBottom: "1px solid #ccc", marginBottom: "10px" }}>
            <h3>{news.headlines}</h3>
            <p><b>Author:</b> {news.author}</p>
            <p><b>Date:</b> {news.newsDate}</p>
            <img src={news.newsImgURL} alt="news" style={{ maxWidth: "200px" }} />
            <p>{news.fullNews}</p>
            <a href={news.newsURL} target="_blank" rel="noopener noreferrer">Read full article</a>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CyberNewsApp;
