// Components/NewsList.js
import React, { useEffect, useState } from "react";
import axios from "axios";

function NewsList({ apiUrl, itemType }) {
  const [articles, setArticles] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const options = {
  method: 'GET',
  url: 'https://cybersecurity-news.p.rapidapi.com/news/guardian', // full URL
  headers: {
    'X-RapidAPI-Key': 'bcbd5c9dfcmsh54b7daa02a69f1bp16b4a1jsn78732abf5a63',   // replace with your real key
    'X-RapidAPI-Host': 'cybersecurity-news.p.rapidapi.com'
  }
};


axios.request(options)
  .then(response => {
    console.log(response.data); // ✅ This will log the API response
  })
  .catch(error => {
    console.error("Error fetching news:", error);
  });
}catch (error) {
        console.error("Error fetching news:", error);
      }
    };

    fetchData();
  }, [apiUrl]);

  return (
    <div style={{ padding: "20px" }}>
      <h2>{itemType ? itemType : "Cybersecurity News"}</h2>
      <ul>
        {articles.length > 0 ? (
          articles.map((article, index) => (
            <li key={index}>
              <a href={article.link} target="_blank" rel="noreferrer">
                {article.title}
              </a>
            </li>
          ))
        ) : (
          <p>No articles found.</p>
        )}
      </ul>
    </div>
  );
}

export default NewsList;
