import React from "react";
import Glossary from "./Glossary";

function NewsItem({ item, itemType }) {
  if (itemType === "news") {
    return (
      <div className="card">
        <h3>{item.title}</h3>
        <p>{item.description}</p>
        <Glossary text={item.description} />
        <a href={item.url} target="_blank" rel="noopener noreferrer">Read More</a>
      </div>
    );
  } else { // vulnerabilities
    return (
      <div className="card">
        <h3>{item.cve ? item.cve.CVE_data_meta.ID : "Unknown ID"}</h3>
        <p>{item.cve ? item.cve.description.description_data.value : ""}</p>
        <Glossary text={item.cve ? item.cve.description.description_data.value : ""} />
        <a href={item.cve ? `https://nvd.nist.gov/vuln/detail/${item.cve.CVE_data_meta.ID}` : "#"} target="_blank" rel="noopener noreferrer">Details</a>
      </div>
    );
  }
}

export default NewsItem;
