import React, { useEffect, useState } from "react";
import axios from "axios";
import NewsItem from "./NewsItem";

function NewsList({ apiUrl, itemType }) {
  const [items, setItems] = useState([]);

  useEffect(() => {
    axios.get(apiUrl)
      .then(res => {
        setItems(res.data.articles || res.data.vulnerabilities || []);
      })
      .catch(() => {
        setItems([]);
      });
  }, [apiUrl]);

  return (
    <div>
      <h2>{itemType === "news" ? "Cyber News" : "Vulnerabilities"}</h2>
      {items.length === 0 ? (
        <p>No data found.</p>
      ) : (
        items.map(item => (
          <NewsItem key={item.title || item.id} item={item} itemType={itemType} />
        ))
      )}
    </div>
  );
}

export default NewsList;
