import React from "react";
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from "react-router-dom";

const NEWS_API_KEY = "9ec3cd1c0603415fa22623a3313343bf";
const NEWS_API_URL = `https://newsapi.org/v2/everything?q=cyber%20security&language=en&sortBy=publishedAt&apiKey=${NEWS_API_KEY}`;
const NVD_API_URL =
  "https://services.nvd.nist.gov/rest/json/cves/1.0?resultsPerPage=10&startIndex=0&pubStartDate=2025-01-01T00:00:00:000 UTC-00:00";

function formatCveDate(dateStr) {
  return new Date(dateStr).toLocaleDateString();
}

function extractCvssInfo(metrics) {
  if (!metrics) return {};

  const cvssV3 = metrics["cvssV3"];
  const cvssV2 = metrics["cvssV2"];

  if (cvssV3) {
    return {
      score: cvssV3.baseScore,
      vector: cvssV3.vectorString,
      severity: cvssV3.baseSeverity,
      attackVector: cvssV3.attackVector,
      attackComplexity: cvssV3.attackComplexity,
      privilegesRequired: cvssV3.privilegesRequired,
      userInteraction: cvssV3.userInteraction,
      confidentialityImpact: cvssV3.confidentialityImpact,
      integrityImpact: cvssV3.integrityImpact,
      availabilityImpact: cvssV3.availabilityImpact,
    };
  } else if (cvssV2) {
    return {
      score: cvssV2.baseScore,
      vector: cvssV2.vectorString,
      severity: cvssV2.severity,
      attackVector: cvssV2.accessVector,
      accessComplexity: cvssV2.accessComplexity,
      authentication: cvssV2.authentication,
      confidentialityImpact: cvssV2.confidentialityImpact,
      integrityImpact: cvssV2.integrityImpact,
      availabilityImpact: cvssV2.availabilityImpact,
    };
  }
  return {};
}

function News() {
  const [newsList, setNewsList] = React.useState([]);
  const [loading, setLoading] = React.useState(false);
  const [error, setError] = React.useState(null);

  React.useEffect(() => {
    async function fetchNews() {
      setLoading(true);
      setError(null);
      try {
        const response = await fetch(NEWS_API_URL);
        if (!response.ok) throw new Error("Failed to fetch news");
        const data = await response.json();
        setNewsList(data.articles || []);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }
    fetchNews();
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h2>Latest Cyber Security News</h2>
      {loading && <p>Loading news...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}
      {!loading && newsList.length === 0 && <p>No news found.</p>}

      {newsList.map((news, i) => (
        <div key={i} style={{ borderBottom: "1px solid #ccc", marginBottom: "15px", paddingBottom: "15px" }}>
          <h3>{news.title}</h3>
          <p>
            <strong>Source:</strong> {news.source?.name || "Unknown"} |{" "}
            <strong>Published:</strong> {new Date(news.publishedAt).toLocaleString()}
          </p>
          {news.urlToImage && <img src={news.urlToImage} alt="news" style={{ maxWidth: "100%", marginBottom: "10px" }} />}
          <p>{news.description || news.content}</p>
          <a href={news.url} target="_blank" rel="noopener noreferrer">
            Read full article
          </a>
        </div>
      ))}
    </div>
  );
}

function Vulnerabilities() {
  const [vulnerabilities, setVulnerabilities] = React.useState([]);
  const [loading, setLoading] = React.useState(false);
  const [error, setError] = React.useState(null);

  React.useEffect(() => {
    async function fetchVulnerabilities() {
      setLoading(true);
      setError(null);
      try {
        const response = await fetch("http://localhost:4000/api/vulnerabilities"); // <-- proxy URL
        if (!response.ok) throw new Error("Failed to fetch vulnerabilities");
        const data = await response.json();
        setVulnerabilities(data.result?.CVE_Items || []);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }
    fetchVulnerabilities();
  }, []);
}

export default function App() {
  return (
    <Router>
      <div style={{ maxWidth: "900px", margin: "0 auto", fontFamily: "Arial, sans-serif" }}>
        <nav style={{ padding: "10px 20px", borderBottom: "1px solid #ccc", marginBottom: "20px" }}>
          <Link to="/news" style={{ marginRight: "20px" }}>
            News
          </Link>
          <Link to="/vulnerabilities">Vulnerabilities</Link>
        </nav>

        <Routes>
          <Route path="/" element={<Navigate replace to="/news" />} />
          <Route path="/news" element={<News />} />
          <Route path="/vulnerabilities" element={<Vulnerabilities />} />
        </Routes>
      </div>
    </Router>
  );
}
